class AppIntegrityChecker @Inject constructor(
    @ApplicationContext private val context: Context
) {

    private val expectedSignature

= "YOUR_EXPECTED_SIGNATURE_HASH"

    fun verifyAppSignature(): Boolean {
        return try {
            val packageInfo = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.P) {
                context.packageManager.getPackageInfo(
                    context.packageName,
                    PackageManager.GET_SIGNING_CERTIFICATES
                )
            } else {
                @Suppress("DEPRECATION")
                context.packageManager.getPackageInfo(
                    context.packageName,
                    PackageManager.GET_SIGNATURES
                )
            }

            val signatures = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.P) {
                packageInfo.signingInfo.apkContentsSigners
            } else {
                @Suppress("DEPRECATION")
                packageInfo.signatures
            }

            signatures.any { signature ->
                val signatureHash = sha256(signature.toByteArray())
                signatureHash == expectedSignature
            }
        } catch (e: Exception) {
            false
        }
    }

    private fun sha256(data: ByteArray): String {
        val digest = MessageDigest.getInstance("SHA-256")
        val hash = digest.digest(data)
        return Base64.encodeToString(hash, Base64.NO_WRAP)
    }

    fun checkAppIntegrity() {
        if (!verifyAppSignature()) {
            // ì„œëª… ê²€ì¦ ì‹¤íŒ¨ ì‹œ ë³´ì•ˆ ì¡°ì¹˜
            throw SecurityException("ì•± ë¬´ê²°ì„± ê²€ì¦ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.")
